import React, { useState, useEffect } from 'react';
import { BrowserRouter, Routes, Route, useNavigate } from 'react-router-dom';
import axios from 'axios';
import '@/App.css';
import MapView from '@/components/MapView';
import ListView from '@/components/ListView';
import ReportForm from '@/components/ReportForm';
import AdminDashboard from '@/components/AdminDashboard';
import { Toaster } from '@/components/ui/sonner';
import { toast } from 'sonner';

const BACKEND_URL = process.env.REACT_APP_BACKEND_URL;
const API = `${BACKEND_URL}/api`;

function HomePage() {
  const [incidents, setIncidents] = useState([]);
  const [showReportForm, setShowReportForm] = useState(false);
  const [view, setView] = useState('map'); // 'map' or 'list'
  const [filter, setFilter] = useState('all'); // 'all', '2h', '4h'
  const [userLocation, setUserLocation] = useState(null);
  const navigate = useNavigate();

  // Request notification permission
  useEffect(() => {
    if ('Notification' in window && Notification.permission === 'default') {
      Notification.requestPermission();
    }
  }, []);

  // Get user location
  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setUserLocation({
            latitude: position.coords.latitude,
            longitude: position.coords.longitude
          });
        },
        (error) => {
          console.log('Location access denied:', error);
        }
      );
    }
  }, []);

  // Fetch incidents
  const fetchIncidents = async () => {
    try {
      let url = `${API}/incidents`;
      if (filter === '2h') url += '?hours=2';
      if (filter === '4h') url += '?hours=4';

      const response = await axios.get(url);
      setIncidents(response.data);

      // Check for nearby urgent incidents
      if (userLocation && Notification.permission === 'granted') {
        response.data.forEach(incident => {
          if (incident.urgency === 'high') {
            const distance = calculateDistance(
              userLocation.latitude,
              userLocation.longitude,
              incident.latitude,
              incident.longitude
            );

            if (distance <= 500) {
              new Notification('âš ï¸ Urgent Incident Nearby!', {
                body: `${getCategoryEmoji(incident.category)} ${incident.category.toUpperCase()} reported within 500m`,
                icon: '/logo192.png'
              });
            }
          }
        });
      }
    } catch (error) {
      console.error('Error fetching incidents:', error);
    }
  };

  useEffect(() => {
    fetchIncidents();
    const interval = setInterval(fetchIncidents, 30000); // Refresh every 30 seconds
    return () => clearInterval(interval);
  }, [filter, userLocation]);

  const calculateDistance = (lat1, lon1, lat2, lon2) => {
    const R = 6371000; // Earth's radius in meters
    const phi1 = (lat1 * Math.PI) / 180;
    const phi2 = (lat2 * Math.PI) / 180;
    const deltaPhi = ((lat2 - lat1) * Math.PI) / 180;
    const deltaLambda = ((lon2 - lon1) * Math.PI) / 180;

    const a = Math.sin(deltaPhi / 2) * Math.sin(deltaPhi / 2) +
              Math.cos(phi1) * Math.cos(phi2) *
              Math.sin(deltaLambda / 2) * Math.sin(deltaLambda / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));

    return R * c;
  };

  const getCategoryEmoji = (category) => {
    const emojis = {
      protest: 'âš ï¸',
      theft: 'ðŸ’°',
      harassment: 'ðŸš¨'
    };
    return emojis[category] || 'ðŸ“';
  };

  const handleReportSubmit = async (reportData) => {
    try {
      await axios.post(`${API}/incidents`, reportData);
      toast.success('Incident reported successfully');
      setShowReportForm(false);
      fetchIncidents();
    } catch (error) {
      toast.error('Failed to submit report');
      console.error('Error submitting report:', error);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-50">
      {/* Emergency Banner */}
      <div className="bg-gradient-to-r from-rose-600 to-red-600 text-white py-3 shadow-lg">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex flex-col sm:flex-row items-center justify-center gap-2 text-center">
            <p className="font-bold text-lg">ðŸš¨ Emergency? Call 000 for immediate assistance</p>
          </div>
        </div>
      </div>

      {/* Header */}
      <header className="bg-white/80 backdrop-blur-md border-b border-slate-200 sticky top-0 z-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <div className="w-10 h-10 bg-gradient-to-br from-blue-500 to-indigo-600 rounded-xl flex items-center justify-center">
                <span className="text-white text-xl font-bold">M</span>
              </div>
              <div>
                <h1 className="text-2xl font-bold text-slate-900">Community Map</h1>
                <p className="text-xs text-slate-600">Stay Safe - Report incidents, find help, discover local opportunities</p>
              </div>
            </div>
            <button
              data-testid="admin-dashboard-button"
              onClick={() => navigate('/admin')}
              className="px-4 py-2 text-sm font-medium text-slate-700 hover:text-slate-900 hover:bg-slate-100 rounded-lg transition-colors"
            >
              Admin
            </button>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6 space-y-6">
        {/* Controls */}
        <div className="flex flex-col sm:flex-row gap-4 items-start sm:items-center justify-between">
          <div className="flex flex-wrap gap-2">
            <button
              data-testid="view-toggle-map"
              onClick={() => setView('map')}
              className={`px-4 py-2 rounded-lg font-medium transition-all ${
                view === 'map'
                  ? 'bg-gradient-to-r from-blue-500 to-indigo-600 text-white shadow-lg shadow-blue-500/30'
                  : 'bg-white text-slate-700 hover:bg-slate-50 border border-slate-200'
              }`}
            >
              Map View
            </button>
            <button
              data-testid="view-toggle-list"
              onClick={() => setView('list')}
              className={`px-4 py-2 rounded-lg font-medium transition-all ${
                view === 'list'
                  ? 'bg-gradient-to-r from-blue-500 to-indigo-600 text-white shadow-lg shadow-blue-500/30'
                  : 'bg-white text-slate-700 hover:bg-slate-50 border border-slate-200'
              }`}
            >
              List View
            </button>
          </div>

          <div className="flex flex-wrap gap-2 items-center">
            <select
              data-testid="time-filter"
              value={filter}
              onChange={(e) => setFilter(e.target.value)}
              className="px-4 py-2 rounded-lg border border-slate-200 bg-white text-slate-700 font-medium focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              <option value="all">All Reports</option>
              <option value="2h">Last 2 Hours</option>
              <option value="4h">Last 4 Hours</option>
            </select>
            <button
              data-testid="report-incident-button"
              onClick={() => setShowReportForm(true)}
              className="px-6 py-2 bg-gradient-to-r from-rose-500 to-pink-600 text-white rounded-lg font-medium hover:shadow-lg hover:shadow-rose-500/30 transition-all"
            >
              + Report Incident
            </button>
          </div>
        </div>

        {/* Info Banner */}
        <div className="bg-amber-50 border border-amber-200 rounded-xl p-4">
          <p className="text-sm text-amber-900">
            <span className="font-semibold">ðŸ“¢ Live Updates:</span> Reports are automatically cleared after 6 hours. Notifications enabled for urgent incidents within 500m.
          </p>
        </div>

        {/* View Container */}
        <div className="bg-white rounded-2xl shadow-xl border border-slate-200 overflow-hidden relative z-10">
          {view === 'map' ? (
            <MapView 
              incidents={incidents} 
              userLocation={userLocation} 
              getCategoryEmoji={getCategoryEmoji}
              onMapClick={(lat, lng) => {
                setShowReportForm(true);
                // Pass coordinates to form - we'll handle this via a state
              }}
            />
          ) : (
            <ListView incidents={incidents} getCategoryEmoji={getCategoryEmoji} />
          )}
        </div>
      </div>

      {/* Report Form Modal */}
      {showReportForm && (
        <ReportForm
          onClose={() => setShowReportForm(false)}
          onSubmit={handleReportSubmit}
          userLocation={userLocation}
        />
      )}

      <Toaster position="top-right" />
    </div>
  );
}

function App() {
  return (
    <div className="App">
      <BrowserRouter>
        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/admin" element={<AdminDashboard />} />
        </Routes>
      </BrowserRouter>
    </div>
  );
}

export default App;
