import React, { useState, useEffect } from 'react';
import { X, MapPin } from 'lucide-react';
import { toast } from 'sonner';
import axios from 'axios';

const BACKEND_URL = process.env.REACT_APP_BACKEND_URL;
const API = `${BACKEND_URL}/api`;

const ReportForm = ({ onClose, onSubmit, userLocation, selectedLocation }) => {
  const [formData, setFormData] = useState({
    category: 'protest',
    urgency: 'medium',
    description: '',
    latitude: selectedLocation?.latitude || userLocation?.latitude || -37.8136,
    longitude: selectedLocation?.longitude || userLocation?.longitude || 144.9631,
    contact_email: '',
    contact_phone: '',
    agreed: false
  });

  const [useGPS, setUseGPS] = useState(false);
  const [locationSource, setLocationSource] = useState(
    selectedLocation ? 'pin' : userLocation ? 'gps' : 'manual'
  );
  const [addressSearch, setAddressSearch] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [isSearching, setIsSearching] = useState(false);
  const [showAddressSearch, setShowAddressSearch] = useState(false);

  useEffect(() => {
    if (userLocation && useGPS) {
      setFormData(prev => ({
        ...prev,
        latitude: userLocation.latitude,
        longitude: userLocation.longitude
      }));
    }
  }, [userLocation, useGPS]);

  const handleAddressSearch = async () => {
    if (!addressSearch.trim()) {
      toast.error('Please enter an address');
      return;
    }

    setIsSearching(true);
    try {
      const response = await axios.post(`${API}/geocode`, { address: addressSearch });
      if (response.data.success && response.data.locations.length > 0) {
        setSearchResults(response.data.locations);
        toast.success(`Found ${response.data.locations.length} location(s)`);
      } else {
        setSearchResults([]);
        toast.error('No locations found. Try a different address.');
      }
    } catch (error) {
      toast.error('Error searching address');
      console.error('Geocoding error:', error);
    } finally {
      setIsSearching(false);
    }
  };

  const selectSearchResult = (location) => {
    setFormData({
      ...formData,
      latitude: location.latitude,
      longitude: location.longitude
    });
    setSearchResults([]);
    setAddressSearch('');
    setShowAddressSearch(false);
    toast.success('Location selected from search');
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!formData.agreed) {
      toast.error('Please agree to the terms before submitting');
      return;
    }

    if (!formData.description.trim()) {
      toast.error('Please provide a description');
      return;
    }

    const submitData = {
      category: formData.category,
      urgency: formData.urgency,
      description: formData.description,
      latitude: parseFloat(formData.latitude),
      longitude: parseFloat(formData.longitude),
      contact_email: formData.contact_email || null,
      contact_phone: formData.contact_phone || null
    };

    onSubmit(submitData);
  };

  return (
    <div className="fixed inset-0 bg-black/50 backdrop-blur-sm flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-2xl shadow-2xl max-w-2xl w-full max-h-[90vh] overflow-y-auto">
        {/* Header */}
        <div className="sticky top-0 bg-gradient-to-r from-blue-500 to-indigo-600 text-white p-6 flex items-center justify-between rounded-t-2xl">
          <h2 className="text-2xl font-bold">Report Incident</h2>
          <button
            data-testid="close-report-form"
            onClick={onClose}
            className="p-2 hover:bg-white/20 rounded-lg transition-colors"
          >
            <X className="w-6 h-6" />
          </button>
        </div>

        <form onSubmit={handleSubmit} className="p-6 space-y-6">
          {/* Category */}
          <div>
            <label className="block text-sm font-semibold text-slate-700 mb-2">
              Incident Category *
            </label>
            <div className="grid grid-cols-3 gap-3">
              {[
                { value: 'protest', emoji: '‚ö†Ô∏è', label: 'Protest' },
                { value: 'theft', emoji: 'üí∞', label: 'Theft' },
                { value: 'harassment', emoji: 'üö®', label: 'Harassment' }
              ].map((cat) => (
                <button
                  key={cat.value}
                  type="button"
                  data-testid={`category-${cat.value}`}
                  onClick={() => setFormData({ ...formData, category: cat.value })}
                  className={`p-4 rounded-xl border-2 transition-all ${
                    formData.category === cat.value
                      ? 'border-blue-500 bg-blue-50 shadow-lg'
                      : 'border-slate-200 hover:border-slate-300'
                  }`}
                >
                  <div className="text-3xl mb-2">{cat.emoji}</div>
                  <div className="text-sm font-medium text-slate-700">{cat.label}</div>
                </button>
              ))}
            </div>
          </div>

          {/* Urgency */}
          <div>
            <label className="block text-sm font-semibold text-slate-700 mb-2">
              Urgency Level *
            </label>
            <div className="grid grid-cols-3 gap-3">
              {[
                { value: 'low', label: 'Low', color: 'emerald' },
                { value: 'medium', label: 'Medium', color: 'amber' },
                { value: 'high', label: 'High', color: 'rose' }
              ].map((urg) => (
                <button
                  key={urg.value}
                  type="button"
                  data-testid={`urgency-${urg.value}`}
                  onClick={() => setFormData({ ...formData, urgency: urg.value })}
                  className={`p-3 rounded-xl border-2 font-medium transition-all ${
                    formData.urgency === urg.value
                      ? `border-${urg.color}-500 bg-${urg.color}-50 text-${urg.color}-700 shadow-lg`
                      : 'border-slate-200 text-slate-700 hover:border-slate-300'
                  }`}
                >
                  {urg.label}
                </button>
              ))}
            </div>
          </div>

          {/* Description */}
          <div>
            <label className="block text-sm font-semibold text-slate-700 mb-2">
              Description *
            </label>
            <textarea
              data-testid="description-input"
              value={formData.description}
              onChange={(e) => setFormData({ ...formData, description: e.target.value })}
              placeholder="Provide details about the incident..."
              rows={4}
              className="w-full px-4 py-3 border border-slate-200 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-transparent resize-none"
              required
            />
          </div>

          {/* Location */}
          <div>
            <label className="block text-sm font-semibold text-slate-700 mb-2">
              Location *
            </label>

            {/* Location Source Indicator */}
            {selectedLocation && (
              <div className="mb-3 p-3 bg-blue-50 border border-blue-200 rounded-xl flex items-center gap-2 text-sm text-blue-800">
                <MapPin className="w-4 h-4" />
                <span className="font-medium">Using pin location from map</span>
              </div>
            )}

            <div className="space-y-3">
              {/* GPS Button */}
              {!selectedLocation && (
                <button
                  type="button"
                  data-testid="use-gps-button"
                  onClick={() => {
                    setUseGPS(!useGPS);
                    setShowAddressSearch(false);
                  }}
                  className={`w-full p-3 rounded-xl border-2 font-medium transition-all flex items-center justify-center gap-2 ${
                    useGPS
                      ? 'border-blue-500 bg-blue-50 text-blue-700'
                      : 'border-slate-200 text-slate-700 hover:border-slate-300'
                  }`}
                >
                  <MapPin className="w-5 h-5" />
                  {useGPS ? 'Using GPS Location' : 'Use My GPS Location'}
                </button>
              )}

              {/* Address Search Toggle */}
              {!selectedLocation && (
                <button
                  type="button"
                  data-testid="address-search-toggle"
                  onClick={() => setShowAddressSearch(!showAddressSearch)}
                  className={`w-full p-3 rounded-xl border-2 font-medium transition-all flex items-center justify-center gap-2 ${
                    showAddressSearch
                      ? 'border-emerald-500 bg-emerald-50 text-emerald-700'
                      : 'border-slate-200 text-slate-700 hover:border-slate-300'
                  }`}
                >
                  üîç {showAddressSearch ? 'Hide Address Search' : 'Search by Street/Address'}
                </button>
              )}

              {/* Address Search Section */}
              {showAddressSearch && (
                <div className="bg-emerald-50 border border-emerald-200 rounded-xl p-4 space-y-3">
                  <div className="flex gap-2">
                    <input
                      data-testid="address-search-input"
                      type="text"
                      value={addressSearch}
                      onChange={(e) => setAddressSearch(e.target.value)}
                      onKeyPress={(e) => e.key === 'Enter' && (e.preventDefault(), handleAddressSearch())}
                      placeholder="e.g., Flinders Street, Melbourne"
                      className="flex-1 px-4 py-2 border border-emerald-300 rounded-lg focus:ring-2 focus:ring-emerald-500 focus:border-transparent bg-white"
                    />
                    <button
                      type="button"
                      data-testid="search-address-button"
                      onClick={handleAddressSearch}
                      disabled={isSearching}
                      className="px-4 py-2 bg-emerald-600 text-white rounded-lg font-medium hover:bg-emerald-700 transition-colors disabled:opacity-50"
                    >
                      {isSearching ? '...' : 'Search'}
                    </button>
                  </div>

                  {/* Search Results */}
                  {searchResults.length > 0 && (
                    <div className="max-h-48 overflow-y-auto space-y-2">
                      {searchResults.map((result, index) => (
                        <button
                          key={index}
                          type="button"
                          data-testid={`search-result-${index}`}
                          onClick={() => selectSearchResult(result)}
                          className="w-full text-left p-3 bg-white border border-emerald-300 rounded-lg hover:bg-emerald-100 transition-colors"
                        >
                          <div className="text-sm text-slate-900">{result.display_name}</div>
                          <div className="text-xs text-slate-500 mt-1">
                            {result.latitude.toFixed(4)}, {result.longitude.toFixed(4)}
                          </div>
                        </button>
                      ))}
                    </div>
                  )}
                </div>
              )}

              {/* Manual Coordinates */}
              <div className="grid grid-cols-2 gap-3">
                <div>
                  <input
                    data-testid="latitude-input"
                    type="number"
                    step="any"
                    value={formData.latitude}
                    onChange={(e) => setFormData({ ...formData, latitude: e.target.value })}
                    placeholder="Latitude"
                    className="w-full px-4 py-2 border border-slate-200 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    required
                  />
                </div>
                <div>
                  <input
                    data-testid="longitude-input"
                    type="number"
                    step="any"
                    value={formData.longitude}
                    onChange={(e) => setFormData({ ...formData, longitude: e.target.value })}
                    placeholder="Longitude"
                    className="w-full px-4 py-2 border border-slate-200 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    required
                  />
                </div>
              </div>
            </div>
          </div>

          {/* Contact Info */}
          <div className="bg-blue-50 border border-blue-200 rounded-xl p-4">
            <h3 className="text-sm font-semibold text-blue-900 mb-3">
              Contact Information (Optional - Increases Credibility)
            </h3>
            <div className="space-y-3">
              <input
                data-testid="contact-email"
                type="email"
                value={formData.contact_email}
                onChange={(e) => setFormData({ ...formData, contact_email: e.target.value })}
                placeholder="Email (visible to admin only)"
                className="w-full px-4 py-2 border border-blue-200 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent bg-white"
              />
              <input
                data-testid="contact-phone"
                type="tel"
                value={formData.contact_phone}
                onChange={(e) => setFormData({ ...formData, contact_phone: e.target.value })}
                placeholder="Phone (visible to admin only)"
                className="w-full px-4 py-2 border border-blue-200 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent bg-white"
              />
            </div>
          </div>

          {/* Agreement */}
          <div className="bg-amber-50 border border-amber-200 rounded-xl p-4">
            <label className="flex items-start gap-3 cursor-pointer">
              <input
                data-testid="agreement-checkbox"
                type="checkbox"
                checked={formData.agreed}
                onChange={(e) => setFormData({ ...formData, agreed: e.target.checked })}
                className="mt-1 w-5 h-5 text-blue-600 border-amber-300 rounded focus:ring-blue-500"
                required
              />
              <span className="text-sm text-amber-900">
                I agree that I am not responsible for any risks or consequences that may arise from using the application, unless those risks are directly caused by a fault within the app itself. I understand that my personal information (if provided) will only be visible to admins for safety purposes.
              </span>
            </label>
          </div>

          {/* Submit Button */}
          <button
            type="submit"
            data-testid="submit-report-button"
            className="w-full py-4 bg-gradient-to-r from-blue-500 to-indigo-600 text-white rounded-xl font-bold text-lg hover:shadow-lg hover:shadow-blue-500/30 transition-all"
          >
            Submit Report
          </button>
        </form>
      </div>
    </div>
  );
};

export default ReportForm;
