import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { ArrowLeft, Trash2, Edit2, Eye, EyeOff } from 'lucide-react';
import { toast } from 'sonner';

const BACKEND_URL = process.env.REACT_APP_BACKEND_URL;
const API = `${BACKEND_URL}/api`;

const AdminDashboard = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [account, setAccount] = useState('');
  const [pin, setPin] = useState('');
  const [incidents, setIncidents] = useState([]);
  const [showContactInfo, setShowContactInfo] = useState({});
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      await axios.post(`${API}/admin/verify`, { account, pin });
      setIsAuthenticated(true);
      toast.success('Admin access granted');
      fetchIncidents();
    } catch (error) {
      toast.error('Invalid account or PIN');
    }
  };

  const fetchIncidents = async () => {
    try {
      const response = await axios.get(`${API}/admin/incidents`);
      setIncidents(response.data);
    } catch (error) {
      toast.error('Failed to fetch incidents');
    }
  };

  const handleDelete = async (incidentId) => {
    if (!window.confirm('Are you sure you want to delete this incident?')) return;

    try {
      await axios.delete(`${API}/admin/incidents/${incidentId}`);
      toast.success('Incident deleted');
      fetchIncidents();
    } catch (error) {
      toast.error('Failed to delete incident');
    }
  };

  const toggleContactInfo = (incidentId) => {
    setShowContactInfo(prev => ({
      ...prev,
      [incidentId]: !prev[incidentId]
    }));
  };

  const getTimeAgo = (date) => {
    const seconds = Math.floor((new Date() - new Date(date)) / 1000);
    if (seconds < 60) return 'Just now';
    if (seconds < 3600) return `${Math.floor(seconds / 60)} min ago`;
    if (seconds < 86400) return `${Math.floor(seconds / 3600)} hr ago`;
    return `${Math.floor(seconds / 86400)} days ago`;
  };

  const getCategoryEmoji = (category) => {
    const emojis = { protest: 'âš ï¸', theft: 'ðŸ’°', harassment: 'ðŸš¨' };
    return emojis[category] || 'ðŸ“';
  };

  const getUrgencyColor = (urgency) => {
    const colors = {
      low: 'bg-emerald-100 text-emerald-700 border-emerald-300',
      medium: 'bg-amber-100 text-amber-700 border-amber-300',
      high: 'bg-rose-100 text-rose-700 border-rose-300'
    };
    return colors[urgency] || 'bg-slate-100 text-slate-700 border-slate-300';
  };

  if (!isAuthenticated) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-slate-800 via-slate-900 to-indigo-900 flex items-center justify-center p-4">
        <div className="bg-white rounded-2xl shadow-2xl max-w-md w-full p-8">
          <div className="text-center mb-8">
            <div className="w-16 h-16 bg-gradient-to-br from-blue-500 to-indigo-600 rounded-xl flex items-center justify-center mx-auto mb-4">
              <span className="text-white text-2xl font-bold">A</span>
            </div>
            <h1 className="text-3xl font-bold text-slate-900 mb-2">Admin Access</h1>
            <p className="text-slate-600">Enter PIN to access dashboard</p>
          </div>

          <form onSubmit={handleLogin} className="space-y-6">
            <div>
              <label className="block text-sm font-semibold text-slate-700 mb-2">
                Admin Account
              </label>
              <input
                data-testid="admin-account-input"
                type="text"
                value={account}
                onChange={(e) => setAccount(e.target.value)}
                placeholder="Enter account name"
                className="w-full px-4 py-3 border border-slate-200 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                required
              />
            </div>

            <div>
              <label className="block text-sm font-semibold text-slate-700 mb-2">
                Admin PIN
              </label>
              <input
                data-testid="admin-pin-input"
                type="password"
                value={pin}
                onChange={(e) => setPin(e.target.value)}
                placeholder="Enter PIN"
                className="w-full px-4 py-3 border border-slate-200 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-transparent text-center text-2xl tracking-widest"
                required
              />
            </div>

            <div className="text-xs text-slate-500 bg-slate-50 p-3 rounded-lg">
              <p><b>Default credentials:</b></p>
              <p>Account: admin</p>
              <p>PIN: 123456</p>
            </div>

            <button
              data-testid="admin-login-button"
              type="submit"
              className="w-full py-3 bg-gradient-to-r from-blue-500 to-indigo-600 text-white rounded-xl font-bold hover:shadow-lg hover:shadow-blue-500/30 transition-all"
            >
              Access Dashboard
            </button>

            <button
              data-testid="back-to-home"
              type="button"
              onClick={() => navigate('/')}
              className="w-full py-3 bg-slate-100 text-slate-700 rounded-xl font-medium hover:bg-slate-200 transition-colors flex items-center justify-center gap-2"
            >
              <ArrowLeft className="w-5 h-5" />
              Back to Home
            </button>
          </form>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-50">
      {/* Header */}
      <header className="bg-white/80 backdrop-blur-md border-b border-slate-200 sticky top-0 z-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <div className="w-10 h-10 bg-gradient-to-br from-blue-500 to-indigo-600 rounded-xl flex items-center justify-center">
                <span className="text-white text-xl font-bold">A</span>
              </div>
              <div>
                <h1 className="text-2xl font-bold text-slate-900">Admin Dashboard</h1>
                <p className="text-xs text-slate-600">{incidents.length} Total Reports</p>
              </div>
            </div>
            <button
              data-testid="logout-button"
              onClick={() => navigate('/')}
              className="px-4 py-2 bg-slate-100 text-slate-700 rounded-lg font-medium hover:bg-slate-200 transition-colors flex items-center gap-2"
            >
              <ArrowLeft className="w-4 h-4" />
              Back to Map
            </button>
          </div>
        </div>
      </header>

      {/* Content */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
        <div className="bg-white rounded-2xl shadow-xl border border-slate-200 p-6">
          {incidents.length === 0 ? (
            <div className="text-center py-12">
              <div className="text-6xl mb-4">ðŸ“Š</div>
              <h3 className="text-xl font-semibold text-slate-700 mb-2">No Incidents</h3>
              <p className="text-slate-500">No reports to moderate at this time.</p>
            </div>
          ) : (
            <div className="space-y-4">
              {incidents.map((incident) => (
                <div
                  key={incident.id}
                  data-testid={`admin-incident-${incident.id}`}
                  className="border border-slate-200 rounded-xl p-4 hover:shadow-lg transition-all"
                >
                  <div className="flex items-start justify-between">
                    <div className="flex items-start space-x-3 flex-1">
                      <div className="text-3xl">{getCategoryEmoji(incident.category)}</div>
                      <div className="flex-1">
                        <div className="flex items-center gap-2 mb-2">
                          <h3 className="text-lg font-bold text-slate-900">
                            {incident.category.toUpperCase()}
                          </h3>
                          <span className={`px-2 py-1 rounded-full text-xs font-semibold border ${getUrgencyColor(incident.urgency)}`}>
                            {incident.urgency.toUpperCase()}
                          </span>
                          {incident.is_verified ? (
                            <span className="px-2 py-1 rounded-full text-xs font-semibold bg-emerald-100 text-emerald-700 border border-emerald-300">
                              âœ“ Verified
                            </span>
                          ) : (
                            <span className="px-2 py-1 rounded-full text-xs font-semibold bg-amber-100 text-amber-700 border border-amber-300">
                              âš  Unverified
                            </span>
                          )}
                        </div>

                        <div className="space-y-2">
                          <p className="text-slate-700">{incident.description}</p>
                          <div className="text-xs text-slate-500">
                            Location: {incident.latitude.toFixed(4)}, {incident.longitude.toFixed(4)}
                          </div>
                          <div className="text-xs text-slate-500">
                            Time: {getTimeAgo(incident.timestamp)} â€¢ Cluster: {incident.cluster_count} report(s)
                          </div>

                          {/* Contact Info */}
                          <div className="mt-3 pt-3 border-t border-slate-200">
                            <button
                              data-testid={`toggle-contact-${incident.id}`}
                              onClick={() => toggleContactInfo(incident.id)}
                              className="flex items-center gap-2 text-sm font-medium text-blue-600 hover:text-blue-700"
                            >
                              {showContactInfo[incident.id] ? (
                                <><EyeOff className="w-4 h-4" /> Hide Contact Info</>
                              ) : (
                                <><Eye className="w-4 h-4" /> Show Contact Info</>
                              )}
                            </button>
                            {showContactInfo[incident.id] && (
                              <div className="mt-2 bg-blue-50 border border-blue-200 rounded-lg p-3 text-sm">
                                <div><b>Email:</b> {incident.contact_email || 'Not provided'}</div>
                                <div><b>Phone:</b> {incident.contact_phone || 'Not provided'}</div>
                              </div>
                            )}
                          </div>
                        </div>
                      </div>
                    </div>

                    <button
                      data-testid={`delete-incident-${incident.id}`}
                      onClick={() => handleDelete(incident.id)}
                      className="ml-2 p-2 text-rose-600 hover:bg-rose-50 rounded-lg transition-colors"
                    >
                      <Trash2 className="w-5 h-5" />
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
